import React from 'react'

class Form extends React.Component {
  constructor() {
    super()
    this.state = {
      fullName: '',
      email: '',
      cpf: '',
      adress: '',

    }
    this.handleChange = this.handleChange.bind(this)
  }

  handleChange = () => {

  }

  render() {
    return (
      <form>
        <fieldset>
          <div>
            <label>Full Name: </label>
            <input
            name = 'fullName'
            type='text'
            value = {this.state.fullName}
            onChange={this.handleChange}
            />
          </div>
          <div>
            <label>E-mail: </label>
            <input
            name = 'email'
            type = 'email'
            value = {this.state.email}
            onChange = {this.handleChange}
            />
          </div>
          <div>
            <label>CPF: </label>
            <input
            name = 'cpf'
            type = 'text'
            value = {this.state.cpf}
            onChange = {this.handleChange}
            />
          </div>
          <div>
            <label>Adress: </label>
            <input
            name = 'cpf'
            type = 'text'
            value = {this.state.cpf}
            onChange = {this.handleChange}
            />
          </div>
          <div>
            <label>City: </label>
            <input
            name = 'city'
            type = 'text'
            value = {this.state.city}
            onChange = {this.handleChange}
            />
          </div>
        </fieldset>
      </form>
    )
  }
}


export default Form
